package terraform.policies.eks.capacity_type
import rego.v1

default exit_code = 0
default messages = {"allow": [], "deny": []}

deny[msg] if {
    module := input.planned_values.root_module.child_modules[_]
    resource := module.resources[_aws_eks_node_group]

    resource.type == "aws_eks_node_group" # Nur Ressourcen vom Typ aws_eks_node_group
    not resource.values.capacity_type == "SPOT" # Bedingung: capacity_type != "SPOT"
    msg := sprintf("The Resource '%s' is using the wrong capacity_type '%s'.", [resource.address, resource.values.capacity_type])
}

exit_code = 1 if {
    count(deny) > 0
}
